tool.command:
ln -s '$input_data_npy_path' ./input_data_npy_path.$input_data_npy_path.ext && ln -s '$input_model_pth_path' ./input_model_pth_path.$input_model_pth_path.ext && #if $config_json: ln -s '$config_json' ./config_json.$config_json.ext && #end if
apply_mdae
#if $config_json: --config ./config_json.$config_json.ext #end if
--input_data_npy_path ./input_data_npy_path.$input_data_npy_path.ext --input_model_pth_path ./input_model_pth_path.$input_model_pth_path.ext --output_reconstructed_data_npy_path $outname_output_reconstructed_data_npy_path #if $outname_output_latent_space_npy_path: --output_latent_space_npy_path $outname_output_latent_space_npy_path #end if ;
mv '$outname_output_reconstructed_data_npy_path' '$output_reconstructed_data_npy_path' && if test -f '$outname_output_latent_space_npy_path'; then mv '$outname_output_latent_space_npy_path' '$output_latent_space_npy_path'; fi;
