tool.command:
#if $input_file.calculate_select == "info_traj":
    ln -s '$input_file.traj_file' './$input_file.traj_file.element_identifier';
    gmx check
        -f './$input_file.traj_file.element_identifier'
        &> 'Trajectory_information_for_"${input_file.traj_file.element_identifier}".txt';
#elif $input_file.calculate_select == "compare_traj":
    ln -s '$input_file.traj_file' './$input_file.traj_file.element_identifier';
    ln -s '$input_file.traj_file2' './$input_file.traj_file2.element_identifier';
    gmx check
        -f './$input_file.traj_file.element_identifier'
        -f2 './$input_file.traj_file2.element_identifier'
        $input_file.rmsd
        -tol '$input_file.rel_tol'
        -abstol '$input_file.abs_tol'
        &> 'Compare_trajectories_between_"${input_file.traj_file.element_identifier}"_and_"${input_file.traj_file2.element_identifier}".txt';
#elif $input_file.calculate_select == "info_struc":
    ln -s '$input_file.struc_file' './$input_file.struc_file.element_identifier';
    gmx check
        -c './$input_file.struc_file.element_identifier'
        -vdwfac '$input_file.vdwfac'
        -bonlo '$input_file.bonlo'
        -bonhi '$input_file.bonhi'
        &> 'Structure_information_for_"${input_file.struc_file.element_identifier}".txt';
#elif $input_file.calculate_select == "info_ener":
    ln -s '$input_file.ener_file' './$input_file.ener_file.element_identifier' ;
    gmx check
        -e './$input_file.ener_file.element_identifier'
        &> 'Energy_information_for_"${$input_file.ener_file.element_identifier}".txt';
#elif $input_file.calculate_select == "info_ind":
    ln -s '$input_file.ind_file' './$input_file.ind_file.element_identifier';
    gmx check
        -n './$input_file.ind_file.element_identifier'
        &> 'Index_information_for_"${input_file.ind_file.element_identifier}".txt';
#elif $input_file.calculate_select == "compare_ener":
    ln -s '$input_file.ener_file' './$input_file.ener_file.element_identifier';
    ln -s '$input_file.ener_file2' './$input_file.ener_file2.element_identifier';
    gmx check
        -e './$input_file.ener_file.element_identifier'
        -e2 './$input_file.ener_file2.element_identifier'
        -tol '$input_file.rel_tol'
        -abstol '$input_file.abs_tol'
        -lastener '$input_file.lastener'
        &> 'Compare_energies_between_"${input_file.ener_file.element_identifier}"_and_"${input_file.ener_file2.element_identifier}".txt';
#else:
    ln -s '$input_file.top_file' './$input_file.top_file.element_identifier';
    ln -s '$input_file.top_file2' './$input_file.top_file2.element_identifier';
    gmx check
        -s1 './$input_file.top_file.element_identifier'
        -s2 './$input_file.top_file2.element_identifier'
        -abstol '$input_file.abs_tol'
        &> 'Compare_topologies_between_"${input_file.top_file.element_identifier}"_and_"${input_file.top_file2.element_identifier}".txt';
#end if
